package j1128.sub.abc;//패키지 컴파일
//한줄 주석
/*
 * public class 프로그램이름(=클래스명){
 * 		public static void main(String[] args) {
 * 			문법에 맞게 코딩
 *  	}
 * }
 * 저장 -> 자동으로 컴파일이 된다.(javac HelloTest2.java) ctrl+s
 * 실행 -> ctrl+F11
 */
public class HelloTest2 {
	 /* 프로그램의 시작과 끝을 실행시켜주는 메소드 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello Java");
		//~println(출력할값) => 출력하고 나서 다음줄에 개행(=줄바꿈)
		//sysout입력후 ctrl+space(자동완성기능)
		System.out.println("동그랑땡");
	}
}
